const initialState = {
  data: []
};

const Reducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_VEHICLE":
      return {
        data: [...state.data, action.payload]
      };
    case "DELETE":
      const newData = state.data.filter(veh => {
        return veh.id != action.id;
      });
      return {
        data: newData
      };

    default:
      return state;
  }
};

export default Reducer;
