import React, { Component } from "react";
import { connect } from "react-redux";
import { addVehicle, deleteVehicle } from "../Redux/Action";
import { Link } from "react-router-dom";

class ViewAllVehicles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      asc: false
    };
  }

  handleSort = () => {
    this.setState({
      asc: !this.state.asc
    });

  };

  render() {
    const { data, deleteVehicle } = this.props;
    console.log(data);
    var totalSales = 0;
    data.forEach(ele => {
      totalSales += parseInt(ele.sales);
    });
    console.log(totalSales);

    if (data.length == 0) {
      return (
        <h1 className="p-5 bg-danger text-white">
          SORRY !! Vehicle database is empty !!!!!
          <Link to="/addVehicle">Go Back</Link>
        </h1>
      );
    } else {
      return (
        <table className="p-5 bg-light table">
          <thead className="bg-dark text-white">
            <tr className="bg-white text-dark">
              <td>
                <Link className="btn btn-outline-info" to="/addVehicle">
                  Add more Vehicle
                </Link>
              </td>
              <td>
                <p className="btn btn-dark text-white">
                  Total Vehicles : {data.length}
                </p>
              </td>
              <td>
                <Link className="btn btn-outline-success" to="/">
                  Go to Dashboard
                </Link>
              </td>
            </tr>
            <tr>
              <td>
                <span>
                  Model Name <button>Sort</button>
                </span>
              </td>
              <td>Sales</td>
              <td>Delete</td>
              <td>More...</td>
            </tr>
          </thead>
          <tbody>
            {data.map(vehicle => {
              return (
                <tr key={vehicle.id}>
                  <td>{vehicle.model_name}</td>
                  <td>{vehicle.sales}</td>
                  <td>
                    <button
                      onClick={() => {
                        deleteVehicle(vehicle.id);
                      }}
                      className="bg-danger px-2 text-white"
                    >
                      X
                    </button>
                  </td>
                  <td>
                    <button className="bg-success px-2">
                      <Link to="/">More..</Link>
                    </button>
                  </td>
                </tr>
              );
            })}
            <tr className="bg-dark text-white">
              <td>
                <h4>Total Sales</h4>
              </td>
              <td>{totalSales}</td>
            </tr>
          </tbody>
        </table>
      );
    }
  }
}

const mapStateToProps = state => {
  return {
    data: state.data
  };
};
const mapDispatchToProps = dispatch => {
  return {
    addVehicle: vehicle => dispatch(addVehicle(vehicle)),
    deleteVehicle: id => dispatch(deleteVehicle(id))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ViewAllVehicles);
