import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import addVehicle from "../Redux/Action";
import { connect } from "react-redux";

export class AddVehicle extends Component {
  constructor(props) {
    super(props);
    this.state = {
      model_name: "",
      vehicle_type: "",
      mileage: "",
      top_speed: "",
      cost: "",
      sales: ""
    };
  }
  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  submit = () => {
    const {
      model_name,
      vehicle_type,
      mileage,
      top_speed,
      cost,
      sales
    } = this.state;


  };
  render() {
    return (
      <div className="container d-flex justify-content-center align-items-center p-2">
        <form className="flex-column col-md-5 col-12 bg-light  border p-2 d-flex">
          <h2 className="text-center text-danger">Add New Vehicle</h2>
          <TextField
            className=" m-2"
            id="outlined-basic"
            onChange={this.handleChange}
            name="model_name"
            label="Model Name"
            variant="outlined"
          />
          <TextField
            id="outlined-basic"
            className="m-2"
            onChange={this.handleChange}
            name="vehicle_type"
            label="Vehicle Type"
            placeholder="SUV, Hatchback etc"
            variant="outlined"
          />
          <TextField
            id="outlined-basic"
            className=" m-2"
            onChange={this.handleChange}
            name="mileage"
            label="Mileage"
            variant="outlined"
          />
          <TextField
            id="outlined-basic"
            className=" m-2"
            onChange={this.handleChange}
            name="top_speed"
            label="Top Speed"
            variant="outlined"
          />
          <TextField
            id="outlined-basic"
            className=" m-2"
            onChange={this.handleChange}
            name="cost"
            label="Cost"
            variant="outlined"
          />
          <TextField
            id="outlined-basic"
            className=" m-2"
            onChange={this.handleChange}
            name="sales"
            label="Sales"
            placeholder="sales in units for FY19-20"
            variant="outlined"
          />
          <Button
            onClick={this.submit}
            variant="outlined"
            className="py-2  m-2 bg-dark text-white"
            color="primary"
          >
            Add Vehicle
          </Button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    data: state.data
  };
};
const mapDispatchToProps = dispatch => {
  return {
    addVehicle: vehicle => dispatch(addVehicle(vehicle))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(AddVehicle);
